/*global chrome*/

const Timer = (props) => {
  const tickHandler = () => {
    if (props.timerID === false) {
      chrome.storage.sync.set({
        startedRecordingAt: null,
      });

      chrome.storage.sync.set({
        savedTime: props.value,
      });
    } else {
      props.onTick();
      props.modifyTimerID(
        setTimeout(() => {
          tickHandler();
        }, 1000)
      );
    }
  };

  const startHandler = () => {
    if (props.timerID === false) {
      props.modifyTimerID(
        setTimeout(() => {
          tickHandler();
        }, 1000)
      );

      chrome.storage.sync.get(["startedRecordingAt", "savedTime"], (data) => {
        if (data.startedRecordingAt === null && data.savedTime === null) {
          chrome.storage.sync.set({
            startedRecordingAt: Date.now(),
          });
        } else {
          chrome.storage.sync.set({
            startedRecordingAt: Date.now() - props.value * 1000,
            savedTime: null,
          });
        }
      });
    }
  };

  const stopHandler = () => {
    clearTimeout(props.timerID);
    props.modifyTimerID(false);
  };

  const resetHandler = () => {
    stopHandler(); // pause time recording
    props.onTick(true); // remove time recorded, reset = true

    chrome.storage.sync.set({
      startedRecordingAt: null,
      savedTime: null,
    });
  };

  if (props.value > 0) {
    if (!props.timerID) {
      startHandler();
    }
  }

  return (
    <div>
      <button disabled={props.value < 1} onClick={resetHandler}>
        ⏹️
      </button>
      <button disabled={!props.timerID} onClick={stopHandler}>
        ⏸️
      </button>
      <button disabled={props.timerID} onClick={startHandler}>
        ▶️
      </button>
      <br />
      <div style={{ color: "white" }}>
        {props.timerID}, {props.value}
      </div>
      <br />
    </div>
  );
};

export default Timer;
