import React from "react";
import styles from "./TextInput.module.css";

const TextInput = (props) => {
  return (
    <div className="row">
      <span>
        <input
          className="clean-slide"
          id={props.id}
          placeholder={props.placeholder}
        />
        <label for={props.id}>{props.text}</label>
      </span>
    </div>
  );
};

export default TextInput;
