import react from "react";
import { useState } from "react";

const AnimatedCounter = (props) => {
  const [oldValue, setOldValue] = useState(props.value);
  const [incrementSize, setIncrementSize] = useState(0);
  const sign = (number) => (number < 0 ? -1 : 1);

  const delayInSeconds = 3;
  const Herz = 25;

  if (incrementSize !== 0) {
    if (+oldValue * sign(incrementSize) >= +props.value * sign(incrementSize)) {
      // we've finished incrementing, let's reset incrementSize
      setIncrementSize(0);
    } else {
      // there's an increment already ongoing, let's keep adding
      setOldValue((prevState) => prevState + 4);
    }
  } else {
    if (+oldValue !== +props.value) {
      // we get new value, set up increment
      setIncrementSize((props.value - oldValue) / (delayInSeconds * Herz));
    }
  }

  return oldValue;
};

