import styles from "./ItemData.module.css";

const ItemData = (props) => {
  const getETA = () => {
    let totalProgress = props.progress - props.itemMin;
    let progressLeft = props.itemMax - props.progress;
    let totalMins = props.time / 60;

    if (props.time < 1 || totalProgress === 0) {
      return "∞";
    }

    if (progressLeft === 0) {
      return "✅";
    }

    const designDigit = (num) => (num < 10 ? "0" + num : num);

    let paceInMins = totalProgress / totalMins;
    let minsPerItem = 1 / paceInMins;
    let minsLeft = minsPerItem * progressLeft;
    // let hoursLeft = minsLeft / 60;
    let millisecondsLeft = minsLeft * 60 * 1000;
    let ETADate = new Date(Date.now() + millisecondsLeft);
    let dateFormatted =
      designDigit(ETADate.getHours()) + ":" + designDigit(ETADate.getMinutes());

    return dateFormatted;
  };

  return (
    <div>
      <form onSubmit={(event) => event.preventDefault()}>
        <input
          type="number"
          min="0"
          step="1"
          onChange={props.onMinChange}
          value={props.itemMin}
        />
        <br />
        <input
          type="number"
          onChange={props.onCurrentChange}
          min="0"
          step="1"
          value={props.progress}
        />
        <br />
        <input
          type="number"
          onChange={props.onMaxChange}
          value={props.itemMax}
          min="0"
          step="1"
        />
        <br />
        <br />
        <div class={styles.progress}>
          <div
            class={styles[`progress-value`]}
            style={{ width: 1 }}
            animatenow="1"
          ></div>
        </div>
        <br />
        Total: {getETA()} ({props.time})<br />
        <button
          onClick={() => {
            props.onCurrentChange({ target: { value: props.progress + 1 } });
          }}
          type="submit"
          autofocus="true"
        >
          Raise
        </button>
      </form>
    </div>
  );
};

export default ItemData;
