import logo from "./logo.svg";
import "./App.css";
import ItemData from "./components/ItemData";
import Timer from "./components/Timer";
import react from "react";
import { useState } from "react";

function App() {
  let [getTime, setTime] = useState(0);
  let [getStart, setStart] = useState(0);
  let [getCurrent, setCurrent] = useState(0);
  let [getEnd, setEnd] = useState(0);

  const incrementMinutes = () => {
    setTime((prevState) => prevState + 1);
  };

  const minHandler = (event) => {
    if (+event.target.value <= getCurrent) {
      setStart(+event.target.value);
    }
  };
  const currentHandler = (event) => {
    if (+event.target.value >= getStart) {
      if (+event.target.value <= getEnd) {
        setCurrent(+event.target.value);
      }
    }
  };
  const maxHandler = (event) => {
    if (+event.target.value >= getCurrent) {
      setEnd(+event.target.value);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <p>
          <Timer value={getTime} onTick={incrementMinutes} />

          <ItemData
            itemMin={getStart}
            itemMax={getEnd}
            progress={getCurrent}
            onMinChange={minHandler}
            onCurrentChange={currentHandler}
            onMaxChange={maxHandler}
            time={getTime}
            value={getTime}
            onTick={incrementMinutes}
          />
        </p>
      </header>
    </div>
  );
}

export default App;
