import { useState } from "react";

const Timer = (props) => {
  const tickHandler = () => {
    props.onTick();
    props.setTimerID(
      setTimeout(() => {
        tickHandler();
      }, 1000)
    );
  };

  const startHandler = () => {
    if (!timerID) {
      props.setTimerID(
        setTimeout(() => {
          tickHandler();
        }, 1000)
      );
    }
  };

  const stopHandler = () => {
    clearTimeout(props.timerID);
    props.setTimerID(false);
  };

  return (
    <div>
      <button disabled={!props.timerID} onClick={stopHandler}>
        ⏸️
      </button>
      <button disabled={props.timerID} onClick={startHandler}>
        ▶️
      </button>
    </div>
  );
};

export default Timer;
