/*global chrome*/
import "./App.css";
import ItemData from "./components/ItemData";
import Timer from "./components/Timer";
import react from "react";
import { useState } from "react";

function App() {
  let [getTime, setTime] = useState(
    chrome.storage.sync.get(["startedRecordingAt"]) === null
      ? 0
      : (Date.now() - chrome.storage.sync.get(["startedRecordingAt"])) / 1000
  );
  let [getStart, setStart] = useState(chrome.storage.sync.get(["start"]));
  let [getCurrent, setCurrent] = useState(
    chrome.storage.sync.get(["progress"])
  );
  let [getEnd, setEnd] = useState(chrome.storage.sync.get(["end"]));
  const [getTimersID, setTimersID] = useState(
    chrome.storage.sync.get(["isTimerOn"])
  );

  const changeHandler = (reset = false) => {
    setTime((prevState) => (reset ? 0 : prevState + 1));
  };

  const minHandler = (event) => {
    if (+event.target.value <= getCurrent) {
      setStart(+event.target.value);
      chrome.storaget.sync.set({ start: +event.target.value });
    }
  };
  const currentHandler = (event) => {
    if (+event.target.value >= getStart) {
      if (+event.target.value <= getEnd) {
        setCurrent(+event.target.value);
        chrome.storaget.sync.set({ progress: +event.target.value });
      }
    }
  };
  const maxHandler = (event) => {
    if (+event.target.value >= getCurrent) {
      setEnd(+event.target.value);
      chrome.storaget.sync.set({ end: +event.target.value });
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <p>
          <Timer
            value={getTime}
            onTick={changeHandler}
            timerID={getTimersID}
            setTimerID={(id) => setTimersID(id)}
          />
          <ItemData
            itemMin={getStart}
            itemMax={getEnd}
            progress={getCurrent}
            onMinChange={minHandler}
            onCurrentChange={currentHandler}
            onMaxChange={maxHandler}
            time={getTime}
            timerID={getTimersID}
            setTimerID={(id) => setTimersID(id)}
          />
        </p>
      </header>
    </div>
  );
}

export default App;
