import react from "react";
import { useState } from "react";

const AnimatedCounter = (props) => {
  const [oldValue, setOldValue] = useState(0); //useState(props.value);
  const [timerID, setTimerID] = useState(false);

  const delayInSeconds = 2;
  const refreshPerSecond =
    props.refreshRate === undefined ? 25 : props.refreshRate;
  const sign = (number) => (number < 0 ? -1 : 1);

  const executeTick = (oldVal, incSize) => {
    if (
      (+oldVal + +incSize) * sign(+incSize) >=
      +props.value * sign(+incSize)
    ) {
      // we've finished incrementing, let's reset the timer and oldValue
      setOldValue(props.value);

      clearTimeout(timerID);
      setTimerID(false);
    } else {
      setOldValue(oldVal + incSize);

      setTimerID(
        setTimeout(
          () => executeTick(oldVal + incSize, incSize),
          1000 * (1 / refreshPerSecond)
        )
      );
    }
  };

  if (+oldValue !== +props.value && !timerID) {
    // we get new value, set up increment
    let tempInc =
      (+props.value - +oldValue) / (delayInSeconds * refreshPerSecond);
    tempInc =
      props.minInc === undefined ? tempInc : Math.max(tempInc, props.minInc);
    // set minimum increment, if defined
    setTimerID(
      setTimeout(
        () => executeTick(oldValue + tempInc, tempInc),
        1000 * (1 / refreshPerSecond)
      )
    );
    return Math.floor(100 * (oldValue + tempInc)) / 100;
  }

  return Math.floor(100 * oldValue) / 100;
};

export default AnimatedCounter;
