const ItemData = (props) => {
  const getETA = () => {
    if (props.time < 1 || props.progress - props.itemMin === 0) {
      return "∞";
    }
    return;
  };

  return (
    <div>
      <input
        type="number"
        min="0"
        step="1"
        onChange={props.onMinChange}
        value={props.itemMin}
      />
      <br />
      <input
        type="number"
        onChange={props.onCurrentChange}
        min="0"
        step="1"
        value={props.progress}
      />
      <br />
      <input
        type="number"
        onChange={props.onMaxChange}
        value={props.itemMax}
        min="0"
        step="1"
      />
      <br />
      <br />
      Total: {getETA()} ({props.time})
    </div>
  );
};

export default ItemData;
