import react from "react";
import { useState } from "react";

const AnimatedCounter = (props) => {
  const [oldValue, setOldValue] = useState(0); //useState(props.value);
  const [incrementSize, setIncrementSize] = useState(0);
  const [timerID, setTimerID] = useState(false);

  const delayInSeconds = 3;
  const refreshPerSecond = 3;
  const sign = (number) => (number < 0 ? -1 : 1);

  const executeTick = (oldVal, incSize) => {
    setOldValue(oldVal + incrementSize);
    // alert(incrementSize);
    if (
      (+oldVal + incSize) * sign(incrementSize) >=
        +props.value * sign(incrementSize) &&
      incrementSize !== 0
    ) {
      // we've finished incrementing, let's reset incrementSize
      setIncrementSize(0);
      clearTimeout(timerID);
      setTimerID(false);
    }
    setTimerID(setTimeout(() => executeTick(incSize), 1000));
  };

  if (+oldValue !== +props.value && incrementSize === 0) {
    // we get new value, set up increment
    alert((props.value - oldValue) / (delayInSeconds * refreshPerSecond));
    if (!timerID) {
      setTimerID(
        setTimeout(
          () =>
            executeTick(
              oldValue,
              (props.value - oldValue) / (delayInSeconds * refreshPerSecond)
            ),
          1000
        )
      );
    }
  }

  return oldValue;
};

//   const tickHandler = async (oldVal, newVal) => {
//     const delayInSeconds = 3;

//     const Herz = 25;
//     const incrementSize = (newVal - oldVal) / (delayInSeconds * Herz);

//     // for (let i = 1; i < delayInSeconds * Herz; i++) {
//     //   await setTimeout(
//     //     () => setOldValue(oldVal + i * incrementSize),
//     //     1000 / Herz
//     //   );
//     // }
//     // await setTimeout(() => setOldValue(newVal), 1000 / Herz);
//   };

export default AnimatedCounter;
