import { useState } from "react";

const Timer = (props) => {
  const [timerID, setTimerID] = useState(false);

  const tickHandler = () => {
    props.onTick();
    setTimerID(
      setTimeout(() => {
        tickHandler();
      }, 1000)
    );
  };

  const startHandler = () => {
    if (!timerID) {
      setTimerID(
        setTimeout(() => {
          tickHandler();
        }, 1000)
      );
    }
  };

  const stopHandler = () => {
    clearTimeout(timerID);
    setTimerID(false);
  };

  return (
    <div>
      <button disabled={!timerID} onClick={stopHandler}>
        ⏸️
      </button>
      <button disabled={timerID} onClick={startHandler}>
        Start
      </button>
    </div>
  );
};

export default Timer;
