/*global chrome*/

const Timer = (props) => {
  const tickHandler = () => {
    props.onTick();
    // props.modifyTimerID(
    //   setTimeout(() => {
    //     tickHandler();
    //   }, 1000)
    // );
  };

  const startHandler = () => {
    if (props.timerID.current === false) {
      props.modifyTimerID(
        setInterval(() => {
          tickHandler();
        }, 1000)
      );

      chrome.storage.sync.get(["startedRecordingAt", "savedTime"], (data) => {
        if (data.startedRecordingAt === null && data.savedTime === null) {
          chrome.storage.sync.set({
            startedRecordingAt: Date.now(),
          });
        } else {
          chrome.storage.sync.set({
            startedRecordingAt: Date.now() - props.value * 1000,
            savedTime: null,
          });
        }
      });
    }
  };

  const stopHandler = () => {
    clearInterval(props.timerID.current);
    props.modifyTimerID(false);

    chrome.storage.sync.set({
      startedRecordingAt: null,
    });

    chrome.storage.sync.set({
      savedTime: props.value,
    });
  };

  const resetHandler = () => {
    stopHandler(); // pause time recording
    props.onTick(true); // remove time recorded -> reset === true

    chrome.storage.sync.set({
      startedRecordingAt: null,
      savedTime: null,
    });
  };

  chrome.storage.sync.get(["startedRecordingAt", "savedTime"], (data) => {
    if (data.startedRecordingAt !== null && data.savedTime === null) {
      startHandler();
    }
  });

  return (
    <div>
      <button disabled={props.value < 1} onClick={resetHandler}>
        ⏹️
      </button>
      <button disabled={!props.timerID.current} onClick={stopHandler}>
        ⏸️
      </button>
      <button disabled={props.timerID.current} onClick={startHandler}>
        ▶️
      </button>
      <br />
      <div style={{ color: "white" }}>
        {props.timerID.current === false ? "false" : props.timerID.current},{" "}
        {props.value}
      </div>
      <br />
    </div>
  );
};

export default Timer;
