import { clear } from "@testing-library/user-event/dist/clear";
import { useState, useEffect } from "react";
import "./BufferElement.css";

const BufferElement = (props) => {
  let [message, setMessage] = useState(props.isActive);

  useEffect(() => {
    let cleaningTimer;

    if (props.isActive === false) {
      setMessage(<div style={{ color: green }}>âœ”</div>);
      cleaningTimer = setTimeout(() => {
        setMessage(false);
      }, 1000);
    } else {
      setMessage(
        <div className="lds-roller">
          <div></div>
          <div></div>
          <div></div>
          <div></div>
          <div></div>
          <div></div>
          <div></div>
          <div></div>
        </div>
      );
    }
    return () => {
      clearTimeout(cleaningTimer);
    };
  }, [props.isActive]);
  return message;
};

export default BufferElement;
