let timerID = false;

const Timer = (props) => {
  const tickHandler = () => {
    props.onTick();
    timerID = setTimeout(() => {
      tickHandler();
    }, 1000);
  };

  const startHandler = () => {
    if (!timerID) {
      timerID = setTimeout(() => {
        tickHandler();
      }, 1000);
    }
  };

  const stopHandler = () => {
    clearTimeout(timerID);
    timerID = false;
  };

  return <div></div>;
};

export default Timer;
