/*global chrome*/

const Timer = (props) => {
  const tickHandler = () => {
    props.onTick();
    props.setTimerID(
      setTimeout(() => {
        tickHandler();
      }, 1000)
    );
  };

  const startHandler = () => {
    if (!props.timerID) {
      chrome.storage.sync.get(["startedRecordingAt"], (data) => {
        if (data === null) {
          chrome.storage.sync.set({ startedRecordingAt: Date.now });
        } else {
          chrome.storage.sync.set({
            startedRecordingAt: Date.now - props.value * 1000,
          });
        }
      });

      props.setTimerID(
        setTimeout(() => {
          tickHandler();
        }, 1000)
      );
    }
  };

  const stopHandler = () => {
    clearTimeout(props.timerID);
    props.setTimerID(false);
    chrome.storage.sync.set({
      startedRecordingAt: Date.now - props.value * 1000,
    });

    chrome.storage.sync.set({
      isTimerOn: false,
    });
  };

  const resetHandler = () => {
    stopHandler(); // pause time recording
    props.onTick(true); // remove time recorded, reset = true
    chrome.storage.sync.set({
      startedRecordingAt: null,
    });
  };

  if (props.timerID === true) {
    props.setTimerID(false);
    startHandler();
  }

  return (
    <div>
      <button disabled={props.value < 1} onClick={resetHandler}>
        ⏹️
      </button>
      <button disabled={!props.timerID} onClick={stopHandler}>
        ⏸️
      </button>
      <button disabled={props.timerID} onClick={startHandler}>
        ▶️
      </button>
    </div>
  );
};

export default Timer;
