import react from "react";
import { useState } from "react";

const AnimatedCounter = (props) => {
  const [oldValue, setOldValue] = useState(props.value);
  const [incrementSize, setIncrementSize] = useState(0);
  const [timerID, setTimerID] = useState(false);

  const delayInSeconds = 3;
  const refreshPerSecond = 3;
  const sign = (number) => (number < 0 ? -1 : 1);

  if (incrementSize !== 0) {
    if (
      (+oldValue + incrementSize) * sign(incrementSize) >=
      +props.value * sign(incrementSize)
    ) {
      // we've finished incrementing, let's reset incrementSize
      setIncrementSize(0);
      clearInterval(timerID);
      setTimerID(false);
    } else {
      // there's an increment already ongoing, let's keep adding
      //   setOldValue((prevState) => prevState + incrementSize);
    }
  } else {
    if (+oldValue !== +props.value) {
      // we get new value, set up increment
      setIncrementSize((props.value - oldValue) / 2);
      setTimerID = setInterval(() => {
        setOldValue((prevState) => prevState + incrementSize);
      }, 300);
    }
  }

  return oldValue + incrementSize;
};

