            class="icon-background"
            cx="40"
            cy="40"
            r="35"
            fill="#21cd92"
            stroke="#21cd92"
            stroke-width="8"
          />
          <div class="abs-center icon check"></div>
          <div class="abs-center icon error"></div>
        </svg>
      </div>
    </div>
  );
};

export default ProgressBar;
