/*global chrome*/
import "./App.css";
import ItemData from "./components/ItemData";
import Timer from "./components/Timer";
import react from "react";
import { useState } from "react";

function App() {
  let [getTime, setTime] = useState(0);
  let [getStart, setStart] = useState(0);
  let [getCurrent, setCurrent] = useState(0);
  let [getEnd, setEnd] = useState(0);

  chrome.storage.sync.get(["startedRecordingAt"], (data) => {
    alert(data);
    setTime(
      data === null
        ? 0
        : (Date.now() - chrome.storage.sync.get(["startedRecordingAt"])) / 1000
    );
  });

  chrome.storage.sync.get(["start"], (data) => {
    setStart(data);
  });

  chrome.storage.sync.get(["progress"], (data) => {
    setCurrent(data);
  });

  chrome.storage.sync.get(["end"], (data) => {
    setEnd(data);
  });

  const [getTimersID, setTimersID] = useState(false);

  chrome.storage.sync.get(["isTimerOn"], (data) => {
    setTimersID(data);
  });

  const changeHandler = (reset = false) => {
    setTime((prevState) => (reset ? 0 : prevState + 1));
  };

  const minHandler = (event) => {
    if (+event.target.value <= getCurrent) {
      setStart(+event.target.value);
      chrome.storage.sync.set({ start: +event.target.value });
    }
  };
  const currentHandler = (event) => {
    if (+event.target.value >= getStart) {
      if (+event.target.value <= getEnd) {
        setCurrent(+event.target.value);
        chrome.storage.sync.set({ progress: +event.target.value });
      }
    }
  };
  const maxHandler = (event) => {
    if (+event.target.value >= getCurrent) {
      setEnd(+event.target.value);
      chrome.storage.sync.set({ end: +event.target.value });
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <p>
          <Timer
            value={getTime}
            onTick={changeHandler}
            timerID={getTimersID}
            setTimerID={(id) => setTimersID(id)}
          />
          <ItemData
            itemMin={getStart}
            itemMax={getEnd}
            progress={getCurrent}
            onMinChange={minHandler}
            onCurrentChange={currentHandler}
            onMaxChange={maxHandler}
            time={getTime}
            timerID={getTimersID}
            setTimerID={(id) => setTimersID(id)}
          />
        </p>
      </header>
    </div>
  );
}

export default App;
