let timerID = false;

const Timer = (props) => {
  const tickHandler = () => {
    if (!props.active) {
      return;
    }

    props.onTick();
    timerID = setTimeout(() => {
      tickHandler();
    }, 1000);
  };

  const startHandler = () => {
    timerID = setTimeout(() => {
      tickHandler();
    }, 1000);
  };

  const stopHandler = () => {
    clearTimeout(timerID);
  };

  return (
    <div>
      <br />
      <button onClick={stopHandler}>Stop</button>
      <button onClick={startHandler}>Start</button>
    </div>
  );
};

export default Timer;
