import { useState } from "react";
import styles from "./ItemData.module.css";
import Timer from "./Timer";

const ItemData = (props) => {
  const [getIsAnimating, setIsAnimating] = useState(0);
  const getETA = () => {
    let totalProgress = props.progress - props.itemMin;
    let progressLeft = props.itemMax - props.progress;
    let totalMins = props.time / 60;

    if (
      props.time < 1 ||
      totalProgress === 0 ||
      (!props.timerID && props.itemMax != props.progress)
    ) {
      return "∞";
    }

    if (progressLeft === 0) {
      if (props.timerID) {
        clearTimeout(props.timerID);
        props.setTimerID(false);
      }
      return "✅";
    }

    const designDigit = (num) => (num < 10 ? "0" + num : num);

    let paceInMins = totalProgress / totalMins;
    let minsPerItem = 1 / paceInMins;
    let minsLeft = minsPerItem * progressLeft;
    // let hoursLeft = minsLeft / 60;
    let millisecondsLeft = minsLeft * 60 * 1000;
    let ETADate = new Date(Date.now() + millisecondsLeft);
    let dateFormatted =
      designDigit(ETADate.getHours()) + ":" + designDigit(ETADate.getMinutes());

    return "ETA: " + dateFormatted;
  };

  const getPercentage = () => {
    let totalProgressMade = props.progress - props.itemMin;
    let totalProgressPossible = props.itemMax - props.itemMin;

    if (totalProgressPossible === 0) {
      return 0;
    }

    return 100 * (totalProgressMade / totalProgressPossible);
  };

  return (
    <div>
      <form onSubmit={(event) => event.preventDefault()}>
        <input
          style={{ width: `2.7em` }}
          type="number"
          min="0"
          step="1"
          onChange={props.onMinChange}
          value={props.itemMin}
        />
        <input
          style={{ width: `2.7em` }}
          type="number"
          onChange={(event) => {
            props.onCurrentChange(event);
            setIsAnimating(1);
          }}
          min="0"
          step="1"
          value={props.progress}
        />
        <input
          style={{ width: `2.7em` }}
          type="number"
          onChange={props.onMaxChange}
          value={props.itemMax}
          min="0"
          step="1"
        />
        <button
          onClick={() => {
            props.onCurrentChange({ target: { value: props.progress + 1 } });
          }}
          type="submit"
          autofocus="true"
        >
          Raise
        </button>
        <br />

        <Timer
          value={props.value}
          onTick={props.onTick}
          timerID={props.timerID}
          setTimerID={props.setTimerID}
        />
        <span
          style={{
            color: props.timerID ? "rgb(243, 247, 46)" : "rgb(255,255,255)",
          }}
        >
          {Math.floor(props.time / 60)} mins. elapsed
        </span>
        <br />
        {getETA()}
        <br />
        <br />
        <div class={styles.progress}>
          <div
            onAnimationEnd={() => {
              setIsAnimating(0);
            }}
            class={styles[`progress-value`]}
            style={{
              width: getPercentage() + "%",
              background:
                getPercentage() < 100
                  ? getPercentage() < 50
                    ? "rgb(197, 52, 52)"
                    : "rgb(243, 247, 46)"
                  : "rgb(32, 189, 17)",
            }}
            animatenow={getIsAnimating}
          ></div>
        </div>
      </form>
    </div>
  );
};

export default ItemData;
