import react from "react";
import { useState } from "react";

const AnimatedCounter = (props) => {
  const [oldValue, setOldValue] = useState(props.value);

  const tickHandler = async (oldVal, newVal) => {
    await setTimeout();
  };

  if (+oldValue !== +props.value) {
    tickHandler(oldValue, props.value);
  }
  return oldValue;
};

export default AnimatedCounter;
