/*global chrome*/

const Timer = (props) => {
  const tickHandler = () => {
    props.onTick();
    props.setTimerID(
      setTimeout(() => {
        tickHandler();
      }, 1000)
    );
  };

  const startHandler = () => {
    if (!props.timerID) {
      if (chrome.storage.sync.get(["startedRecordingAt"]) === null) {
        chrome.storage.sync.set({ startedRecordingAt: Date.now });
      }
      props.setTimerID(
        setTimeout(() => {
          tickHandler();
        }, 1000)
      );
    }
  };

  const stopHandler = () => {
    clearTimeout(props.timerID);
    props.setTimerID(false);
  };

  const resetHandler = () => {
    stopHandler(); // pause time recording
    props.onTick(true); // remove time recorded
  };
  if (props.timerID === true) {
    props.timerID === false;
    startHandler();
  }
  return (
    <div>
      <button disabled={props.value < 1} onClick={resetHandler}>
        ⏹️
      </button>
      <button disabled={!props.timerID} onClick={stopHandler}>
        ⏸️
      </button>
      <button disabled={props.timerID} onClick={startHandler}>
        ▶️
      </button>
    </div>
  );
};

export default Timer;
