import { useCallback, useState } from "react";
import styles from "./ItemData.module.css";

const ItemData = (props) => {
  const [getProgressElement, setProgressElement] = useState(0);

  const progressRef = useCallback((node) => {
    if (node !== null) {
      setProgressElement(node);
    }
  }, []);

  const getETA = () => {
    let totalProgress = props.progress - props.itemMin;
    let progressLeft = props.itemMax - props.progress;
    let totalMins = props.time / 60;

    if (
      props.time < 1 ||
      totalProgress === 0 ||
      (!props.timerID && props.itemMax != props.progress)
    ) {
      return "∞";
    }

    if (progressLeft === 0) {
      if (props.timerID) {
        clearTimeout(props.timerID);
        props.setTimerID(false);
      }
      return "✅";
    }

    const designDigit = (num) => (num < 10 ? "0" + num : num);

    let paceInMins = totalProgress / totalMins;
    let minsPerItem = 1 / paceInMins;
    let minsLeft = minsPerItem * progressLeft;
    // let hoursLeft = minsLeft / 60;
    let millisecondsLeft = minsLeft * 60 * 1000;
    let ETADate = new Date(Date.now() + millisecondsLeft);
    let dateFormatted =
      designDigit(ETADate.getHours()) + ":" + designDigit(ETADate.getMinutes());

    return dateFormatted;
  };

  const getPercentage = () => {
    let totalProgressMade = props.progress - props.itemMin;
    let totalProgressPossible = props.itemMax - props.itemMin;

    if (totalProgressPossible === 0) {
      return 0;
    }

    return 100 * (totalProgressMade / totalProgressPossible);
  };

  return (
    <div>
      <form onSubmit={(event) => event.preventDefault()}>
        <input
          style={{ width: `2.7em` }}
          type="number"
          className={styles.items}
          min="0"
          step="1"
          onChange={props.onMinChange}
          value={props.itemMin}
          disabled={props.timerID}
        />
        <input
          className={styles.items}
          style={{ width: `2.7em` }}
          type="number"
          onChange={(event) => {
            props.onCurrentChange(event);
          }}
          min="0"
          step="1"
          value={props.progress}
          // disabled={!props.timerID}
        />
        <input
          className={styles.items}
          style={{ width: `2.7em` }}
          type="number"
          onChange={props.onMaxChange}
          value={props.itemMax}
          min="0"
          step="1"
          disabled={props.timerID}
        />
        <button
          className={styles.items}
          onClick={() => {
            props.onCurrentChange({ target: { value: props.progress + 1 } });
          }}
          type="submit"
          disabled={+props.progress === +props.itemMax}
          autofocus="true"
        >
          ⬆️
        </button>
        <br />
        <br />
        <span
          style={{
            color: props.timerID
              ? getPercentage() < 100
                ? getPercentage() < 50
                  ? "rgb(197, 52, 52)"
                  : "rgb(243, 247, 46)"
                : "rgb(32, 189, 17)"
              : props.progress === props.itemMax && props.itemMax !== 0
              ? "rgb(32, 189, 17)"
              : "rgb(255,255,255)",
          }}
        >
          {Math.floor(props.time / 60)} mins. elapsed ⟶
        </span>
        {` ${getETA()}`}
        <br />
        <br />
        <div class={styles.progress}>
          <div
            onResize={(e) => alert("s")}
            // onTransitionEnd={() => {
            //   setIsAnimating(0);
            // }}
            className={styles[`progress-value`]}
            style={{
              width: getPercentage() + "%",
              background:
                getPercentage() < 100
                  ? getPercentage() < 50
                    ? "rgb(197, 52, 52)"
                    : "rgb(243, 247, 46)"
                  : "rgb(32, 189, 17)",
            }}
            ref={progressRef}
          >
            {getPercentage()}:{" "}
            {window
              .getComputedStyle(getProgressElement)
              .getPropertyValue("background-color")}
          </div>
        </div>
      </form>
    </div>
  );
};

export default ItemData;
